const inColdWallet = op.inString("Cold Wallet Address");
const inTransferRate = op.inValue("Transfer Rate", 0);
const inTickSize = op.inValue("Tick Size", 5);
const inDomain = op.inString("Domain", "example.com");

// Boolean checkboxes for AccountSetAsfFlags
const inCheckDefaultRipple = op.inBool("Enable Default Ripple");

// Boolean checkboxes for AccountSetTfFlags
const inCheckNoDirectRipple = op.inBool("Disallow XRP");
const inCheckOptionalDestTag = op.inBool("Optional Destination Tag");

const outTransactionHash = op.outString("Transaction Hash");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");
const outError = op.outString("Error");
const outMissingFields = op.outBool("Missing Fields");

inTrigger.onTriggered = main;
let client = null;

async function main() {


  // Check if required fields are missing
  if (!inWss.get() || !inSeed.get() || !inColdWallet.get()) {
    outError.set("Missing fields");
    outMissingFields.set(true);
    outFailure.trigger();
    return;
  }

  // Define the network client
  try {
    if (client === null) {
      client = new xrpl.Client(inWss.get());
      await client.connect();
    }

    const wallet = xrpl.Wallet.fromSeed(inSeed.get());

    // Prepare transaction object
    const prepared = await client.autofill({
      TransactionType: "AccountSet",
      Account: inColdWallet.get(),
      TransferRate: inTransferRate.get(),
      TickSize: inTickSize.get(),
      Domain: xrpl.convertStringToHex(inDomain.get()),

      // Convert boolean checkboxes to appropriate flag values
      SetFlag: (inCheckDefaultRipple.get() ? xrpl.AccountSetAsfFlags.asfDefaultRipple : 0),

      Flags: (inCheckNoDirectRipple.get() ? xrpl.AccountSetTfFlags.tfDisallowXRP : 0) |
        (inCheckOptionalDestTag.get() ? xrpl.AccountSetTfFlags.tfOptionalDestTag : 0)
    });



    // Sign prepared instructions

    const signed = wallet.sign(prepared);


    // Submit signed transaction
    const submitResult = await client.submitAndWait(signed.tx_blob);

    outTransactionHash.set(submitResult.result.hash);
    outSuccess.trigger();
  } catch (error) {

    outError.set(error.message);
    outFailure.trigger();
  } finally {
    if (client !== null) {
      client.disconnect();
      client = null;
    }
  }
}

//inputs
const inApiKey = op.inString("API Key");
const inTriggerVerification = op.inTriggerButton("Start Verification");
const inGivenName = op.inString("Given Name");
const inLastName = op.inString("Last Name");
const inVendorData = op.inString("Vendor Data");

//outputs
const outSuccess = op.outTrigger("Success");
const outFail = op.outTrigger("Fail");
const outSessionURL = op.outString("Session URL");
const outSessionToken = op.outString("Session Token");
const outFullResponse = op.outObject("Full Response");
const outStarted = op.outTrigger("STARTED");
const outCanceled = op.outTrigger("CANCELED");
const outFinished = op.outTrigger("FINISHED");
const outInitialized = op.outValueBool("Initialized");

let veriff;

inApiKey.onChange = initializeVeriff;
inTriggerVerification.onTriggered = startVerification;

function initializeVeriff(){
    if(!inApiKey.get()){
        outInitialized.set(false);
        return;
    }

    try{
        veriff = Veriff({
            apiKey: inApiKey.get(),
            parentId: 'veriff2',
            onSession: function(err, response) {
                if (err) {
                    outFail.trigger();
                    return;
                }
                console.log("onSessionStarted")
                window.veriffSDK.createVeriffFrame({
                    url: response.verification.url,
                    onEvent: function(msg) {
                        console.log(msg);
                        switch(msg) {
                            case 'STARTED':

                                break;
                            case 'CANCELED':
                                outCanceled.trigger();
                                break;
                            case 'FINISHED':
                                outFinished.trigger();
                                break;
                        }
                    }
                });

                outSessionURL.set(response.verification.url);
                outSessionToken.set(response.verification.sessionToken);
                outFullResponse.set(response);

                outSuccess.trigger();
                outStarted.trigger();
            }
        });

        outInitialized.set(true);
    }catch(e){
        outInitialized.set(false);
    }
}

function startVerification(){
    veriff.setParams({
        person: {
            givenName: inGivenName.get(),
            lastName: inLastName.get()
        },
        vendorData: inVendorData.get()
    });

    veriff.mount();
}

