// input
const inTriggerSignUp = op.inTriggerButton("Sign up");
const inEmail = op.inString("email");
const inPassword = op.inString("password");

// output
const outSignUpErrorMessage = op.outString('Sign Up Error Message', '');
const outSignUpError = op.outValueBool('Sign Up Error', false);
const outLoading = op.outBoolNum("Loading", 0);
const outVerificationEmailSent = op.outValueBool(false);
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTriggerSignUp.onTriggered = signUp;

async function signUp() {
  outLoading.set(1);
  let db = firebase.firestore();

  try {
    const userCredential = await firebase.auth().createUserWithEmailAndPassword(inEmail.get(), inPassword.get())
    const user = userCredential.user;
    const uid = user.uid;

    // User Access Collection
    let userAccessDocRef = db.collection("userAccess").doc(uid);
    await userAccessDocRef.set({
      "type": "client",
      "accountStatus": "active",
      "statusMsg": "new",
      "uid": uid,
      "createdAt": firebase.firestore.FieldValue.serverTimestamp(),
      "emailVerified": "false",
      "lastDecision": "new",
      "lastEvent": "new"
    });

    // Users Collection
    let usersDocRef = db.collection("users").doc(uid);
    await usersDocRef.set({
      "email": user.email,
      "firstName": "unverified",
      "lastName": "user",
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

    // Veriff Collection
    let veriffDocRef = db.collection("veriff").doc(uid);
    await veriffDocRef.set({
      "lastEvent": "new",
      "lastDecision": "new",
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

    if (!user.emailVerified) {
      user.sendEmailVerification();
      outVerificationEmailSent.set(true);
    }
      
    outSignUpError.set(false);
    outSignUpErrorMessage.set('');
    outLoading.set(0);
    outSuccess.trigger();
  } catch (error) {
    outLoading.set(0);
    outSignUpError.set(true);
    outSignUpErrorMessage.set(error.message);
    outFailure.trigger();
  }
}

