async function main() {
    try {
        const secret = inSeed.get(); // Fetch the seed
        const transactionJSON = inTransactionJSON.get(); // Fetch the transaction JSON object

        if(!secret || !transactionJSON) {
            throw new Error("Secret or Transaction JSON is missing.");
        }

        // Generate a wallet object from the seed
        const wallet = xrpl.Wallet.fromSeed(secret);

        // Sign the transaction
        const signedTransaction = wallet.sign(transactionJSON);

        outSignedTransaction.set(signedTransaction); // Set the output as the signed transaction
        outSuccess.trigger(); // Trigger the success output
    } catch (error) {
        outError.set(error.toString()); // Output the error message
        outFailure.trigger(); // Trigger the failure output
    }
}

// Inputs
const inTrigger = op.inTriggerButton("Trigger In");
const inWss = op.inString("WSS URL");
const inSeed = op.inString("Sender Secret Key");
const inAddress = op.inString("Sender Address");
const inTransaction = op.inObject("Transaction");  // New input for the transaction object

// Outputs
const outSignedTx = op.outObject("Signed Transaction");
const outError = op.outString("Error");
const outMissingFields = op.outBool("Missing Fields");

// Variables
let client = null;

// Event listeners
inTrigger.onTriggered = main;

// Functions
function clearOutputs() {
    outSignedTx.set(null);
    outError.set('');
    outMissingFields.set(false);
}

async function main() {
    clearOutputs();

    if (!inWss.get() || !inSeed.get() || !inAddress.get() || !inTransaction.get()) {
        let missingFields = "";
        if (!inWss.get()) {
            missingFields += "WSS URL, ";
        }
        if (!inSeed.get()) {
            missingFields += "Sender Secret Key, ";
        }
        if (!inAddress.get()) {
            missingFields += "Sender Address, ";
        }
        if (!inTransaction.get()) {
            missingFields += "Transaction, ";
        }

        outError.set(missingFields + "should not be empty");
        outMissingFields.set(true);
        return;
    }

    try {
        if (client === null) {
            client = new xrpl.Client(inWss.get());
            await client.connect();
        }

        const wallet = xrpl.Wallet.fromSeed(inSeed.get());

        const transaction = inTransaction.get();  // Use the input transaction object

        const signedTx = await client.request({
            command: 'sign_for',
            tx_json: transaction,
            account: inAddress.get(),
            secret: inSeed.get(),
        });

        outSignedTx.set(signedTx);

    } catch (error) {
        let errorMessage = error.message;

        if (error.data) {
            errorMessage += ' Data: ' + JSON.stringify(error.data);
        }

        if (error.code) {
            errorMessage += ' Code: ' + error.code;
        }

        outError.set(errorMessage);
    } finally {
        if (client !== null) {
            await client.disconnect();
            client = null;
        }
    }
}

