// Input
const dataInputPort = op.inArray("Data Array");

// Output
const dataOutputPort = op.outArray("Modified Data Array");

function formatNumber(num) {
    const [whole, decimal] = num.split('.');
    const formattedWhole = Number(whole).toLocaleString('en');
    let formattedDecimal = (decimal || '').substring(0, 3);

    // If the decimal part is less than 3 digits, add trailing zeroes
    while(formattedDecimal.length < 3) {
        formattedDecimal += '0';
    }

    return `${formattedWhole}.${formattedDecimal}`;
}

dataInputPort.onChange = function() {
    const data = dataInputPort.get();
    if (!data) return;

    const modifiedData = data.map((item) => {
        if(item.hasOwnProperty('balance')) {
            item['balance'] = formatNumber(item['balance']);
        }
        return item;
    });

    dataOutputPort.set(modifiedData);
}

