const
    addTabString = op.inString("Add Tab", ""),
    removeTabString = op.inString("Remove Tab", ""),
    clearTabs = op.inTriggerButton("Clear Tabs"),
    result = op.outString("Result", "Home,");

let tabArray = ["Home"];

addTabString.onChange = function() {
    const newTabName = addTabString.get().trim();
    if (newTabName !== "" && tabArray.indexOf(newTabName) === -1) {
        tabArray.push(newTabName);
        result.set(tabArray.join(",") + ",");
    }
}

removeTabString.onChange = function() {
    if (removeTabString.get() !== "") {
        let index = tabArray.indexOf(removeTabString.get());
        if (index > -1) {
            tabArray.splice(index, 1);
        }
        result.set(tabArray.join(",") + ",");
    }
}

clearTabs.onTriggered = function() {
    tabArray = ["Home"];
    result.set("Home,");
}

result.set("Home,");

// If the number is less than 0 then it will output 0.

const
    number = op.inValue("Number", 0),
    result = op.outValue("Result", 0);

number.onChange = function() {
    const val = number.get() < 0 ? 0 : number.get();
    result.set(val);
}

result.set(0);

