//inputs
const inTriggerSignIn = op.inTriggerButton("Sign in");
const inTriggerSignUp = op.inTriggerButton("Sign up");
const inConfiguration = op.inObject("Configuration");
const inTriggerVerifyEmail = op.inTriggerButton("Trigger Verify Email");
const inTriggerEmailChange = op.inTriggerButton("Trigger Email change");
const inUpdatedEmail = op.inString("Updated Email");
const inTriggerForgetPassword = op.inTriggerButton("Trigger Forget password");
const inForgetPasswordEmail = op.inString("Forget Password email");
const inTriggerNewPassword = op.inTriggerButton("Trigger New Password");
const inNewPassword = op.inString("New password");

//outputs
const outFirebase = op.outObject("Firebase");
const outLoggedIn = op.outValueBool("Logged in", false);
const outLoginError = op.outObject("Authentication error");
const outInitialized = op.outValueBool("Initialized");
const outUser = op.outObject("User");
const outMisssingUpdatedEmail = op.outValueBool("Missing Updated Email", false);
const outEmailUpdated = op.outValueBool("Email Updated", false);
const outMisssingForgetPasswordEmail = op.outValueBool("Missing Reset Password Email", false);
const outForgetPasswordEmailSent = op.outValueBool("Forget Password Email Sent", false);
const outMisssingNewPassword = op.outValueBool("Missing New Password", false);
const outNewPasswordSet = op.outValueBool("New Password Set", false);
const outChangeEmailError = op.outString("Change Email Error");
const outEmailChangeError = op.outTrigger("Email change error");
const outEmailChangeSuccess = op.outTrigger("Email change success");
const outUserLoggedInTrigger = op.outTrigger("User Logged In");
const outUserLoggedOutTrigger = op.outTrigger("User Logged Out");
const outForgetPasswordErrorMessage = op.outString("Forget Password Error Message");
const outForgetPasswordSuccess = op.outTrigger("Forget Password Success");
const outForgetPasswordFail = op.outTrigger("Forget Password Fail");
const outFirstTimeSignUp = op.outTrigger("First Time Sign Up");

let firebaseApp;

inConfiguration.onChange = initializeFirebase;
inTriggerVerifyEmail.onTriggered = sendEmailVerification;
inTriggerEmailChange.onTriggered = updateEmail;
inTriggerForgetPassword.onTriggered = forgetPassword;
inTriggerNewPassword.onTriggered = setNewPassword;

function initializeFirebase() {
    if (firebaseApp || !inConfiguration.get()) {
        outInitialized.set(true);
        return;
    }

    try {
        firebaseApp = firebase.initializeApp(inConfiguration.get());
        outInitialized.set(true);
    } catch (e) {
        outInitialized.set(false);
    }

    firebase.auth().onAuthStateChanged((user) => {
        if (user) {
            outLoggedIn.set(true);
            outUser.set(user);
            outUserLoggedInTrigger.trigger();
        } else {
            outLoggedIn.set(false);
            outUserLoggedOutTrigger.trigger();
        }
    });

    firebase.auth().setPersistence(firebase.auth.Auth.Persistence.LOCAL)
}

outFirebase.set(firebase);
inTriggerSignIn.onTriggered = signIn;
inTriggerSignUp.onTriggered = signUp;

function signIn() {
    var provider = new firebase.auth.GoogleAuthProvider();
    firebase.auth().signInWithPopup(provider)
        .then((result) => {
            outLoggedIn.set(true);
            outUserLoggedInTrigger.trigger();

            if (result.additionalUserInfo.isNewUser) {
                outFirstTimeSignUp.trigger();
            }
        }).catch((error) => {
            outLoginError.set(error);
        });
}

function signUp() {
    var provider = new firebase.auth.GoogleAuthProvider();
    firebase.auth().signInWithPopup(provider)
        .then((result) => {
            if (result.additionalUserInfo.isNewUser) {
                outFirstTimeSignUp.trigger();
            }
        }).catch((error) => {
            outLoginError.set(error);
        });
}

function sendEmailVerification() {
    let user = firebase.auth().currentUser;
    if (user && !user.emailVerified) {
        user.sendEmailVerification();
    }
}

function updateEmail() {
    if (!inUpdatedEmail.get()) {
        outMisssingUpdatedEmail.set(true);
        return;
    }

    const user = firebase.auth().currentUser;
    user.updateEmail(inUpdatedEmail.get())
        .then(() => {
            outMisssingUpdatedEmail.set(false);
            outEmailUpdated.set(true);
            outEmailChangeSuccess.trigger();
        })
        .catch((error) => {
            outEmailUpdated.set(false);
            outEmailChangeError.trigger();
        });
}

function forgetPassword() {
    if (!inForgetPasswordEmail.get()) {
        outMisssingForgetPasswordEmail.set(true);
        return;
    }

    firebase.auth().sendPasswordResetEmail(inForgetPasswordEmail.get())
        .then(() => {
            outMisssingForgetPasswordEmail.set(false);
            outForgetPasswordEmailSent.set(true);
            outForgetPasswordSuccess.trigger();
        })
        .catch((error) => {
            outForgetPasswordEmailSent.set(false);
            outForgetPasswordFail.trigger();
            outForgetPasswordErrorMessage.set(error.message);
        });
}

function setNewPassword() {
    if (!inNewPassword.get()) {
        outMisssingNewPassword.set(true);
        return;
    }

    const user = firebase.auth().currentUser;
    user.updatePassword(inNewPassword.get()).then(() => {
        outMisssingNewPassword.set(false);
        outNewPasswordSet.set(true);
    }).catch((error) => {
        outNewPasswordSet.set(false);
    });
}

