// inputs
const inTriggerGetIdToken = op.inTriggerButton("Get ID Token");

// outputs
const outIdToken = op.outString("ID Token");
const outError = op.outString("Error");
const outSuccess = op.outTrigger("Success");  // success trigger
const outFail = op.outTrigger("Fail");  // fail trigger

inTriggerGetIdToken.onTriggered = getIdToken;

function getIdToken() {
    const user = firebase.auth().currentUser;

    if (user) {
        user.getIdToken(true)
            .then((idToken) => {
                outIdToken.set(idToken);
                outSuccess.trigger();  // trigger success
            })
            .catch((error) => {
                outError.set("Error getting ID token: " + error);
                outFail.trigger();  // trigger fail
            });
    }
}

