// Custom JavaScript code for cables op with Firebase Sign-in

// Inputs
const inTriggerSignIn = op.inTriggerButton("Sign in");
const inEmail = op.inString("email");
const inPassword = op.inString("password");

// Outputs
const outSignUpErrorMessage = op.outString('Sign in error message', '');
const outSignUpError = op.outValueBool('Sign in error', false);
const outUserDisabled = op.outValueBool('User disabled', false);
const outInvalidEmail = op.outValueBool('Invalid email', false);
const outUserNotFound = op.outValueBool('User not found', false);
const outWrongPassword = op.outValueBool('Weak password', false);
const outLoading = op.outBoolNum("Loading", 0);
const outSuccessfulLogin = op.outTrigger("Successful Login");
const outErrorLogin = op.outTrigger("Error Login");

// Sign-in function
inTriggerSignIn.onTriggered = signIn;

async function signIn() {
    outLoading.set(1);
    let db = firebase.firestore();
    await firebase.auth().signInWithEmailAndPassword(inEmail.get(), inPassword.get())
        .then(async (userCredential) => {
            outLoading.set(0);
            outSuccessfulLogin.trigger(); // Trigger Successful Login
        })
        .catch((error) => {
            outLoading.set(0);
            setCorrectError(error);
            outErrorLogin.trigger(); // Trigger Error Login
        });
}

// Reset errors
function resetErrors() {
    outSignUpErrorMessage.set('');
    outSignUpError.set(false);
    outUserDisabled.set(false);
    outInvalidEmail.set(false);
    outUserNotFound.set(false);
    outWrongPassword.set(false);
}

// Set correct error
function setCorrectError(error) {
    outSignUpError.set(true);
    outSignUpErrorMessage.set(error.code);

    if (error.code === 'auth/user-disabled') {
        outUserDisabled.set(true);
        outInvalidEmail.set(false);
        outUserNotFound.set(false);
        outWrongPassword.set(false);
        return;
    }
    if (error.code === 'auth/invalid-email') {
        outUserDisabled.set(false);
        outInvalidEmail.set(true);
        outUserNotFound.set(false);
        outWrongPassword.set(false);
        return;
    }
    if (error.code === 'auth/user-not-found') {
        outUserDisabled.set(false);
        outInvalidEmail.set(false);
        outUserNotFound.set(true);
        outWrongPassword.set(false);
        return;
    }
    if (error.code === 'auth/wrong-password') {
        outUserDisabled.set(false);
        outInvalidEmail.set(false);
        outUserNotFound.set(false);
        outWrongPassword.set(true);
        return;
    }
}

