//input
const inTrigger = op.inTriggerButton("Trigger");
const inCollectionName = op.inString("Collection name");
const inDocumentIDs = op.inArray("Document IDs");
const inSubcollectionName = op.inString("Subcollection name", "");
const inParentDocumentID = op.inString("Parent Document ID", "");

//output
const outError = op.outBoolNum("Error", false);
const outErrorMessage = op.outString("Error Message");
const outDocuments = op.outArray("Documents");
const outDocumentsNotFound = op.outValueBool("Documents Not Found");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered = getDocuments;

function getDocuments() {
    if (!inCollectionName.get() || inDocumentIDs.get().length === 0) {
        outError.set(true);
        outErrorMessage.set(
            "Missing Arguments: Collection name or Document IDs are missing!"
        );
        outDocuments.set([]);
        outDocumentsNotFound.set(false);
        return;
    }

    let db;
    try {
        db = firebase.firestore();
    } catch (e) {
        console.log(e);
    }

    let docRefs = inDocumentIDs.get().map(docId => {
        if(inSubcollectionName.get() && inParentDocumentID.get()) {
            return db.collection(inCollectionName.get())
                      .doc(inParentDocumentID.get())
                      .collection(inSubcollectionName.get())
                      .doc(docId).get();
        } else {
            return db.collection(inCollectionName.get())
                      .doc(docId).get();
        }
    });

    Promise.all(docRefs)
        .then((docs) => {
            let docsData = [];
            docs.forEach(doc => {
                if(doc.exists) {
                    docsData.push(doc.data());
                }
            });

            if(docsData.length > 0) {
                outDocuments.set(docsData);
                outDocumentsNotFound.set(false);
                outSuccess.trigger();
            } else {
                outDocuments.set([]);
                outDocumentsNotFound.set(true);
                outFailure.trigger();
            }
            outError.set(false);
            outErrorMessage.set(null);
        })
        .catch((error) => {
            outError.set(true);
            outErrorMessage.set(error.message);
            outFailure.trigger();
        });
}

// input
const inTrigger = op.inTriggerButton("Add Records");
const inClear = op.inTriggerButton("Clear");
const inArray = op.inArray("New Records");

// output
const outArray = op.outArray("Accumulated Records");

// internal state
let accumulatedArray = [];

// event
inTrigger.onTriggered = accumulateRecords;
inClear.onTriggered = clearRecords;

function accumulateRecords() {
  const newArray = inArray.get();
  accumulatedArray = [...accumulatedArray, ...newArray];
  outArray.set(accumulatedArray);
}

function clearRecords() {
  accumulatedArray = [];
  outArray.set(accumulatedArray);
}

// inputs
const inTrigger = op.inTriggerButton("Trigger");
const inNextPageTrigger = op.inTriggerButton("Next Page Trigger");
const inCollectionName = op.inString("Collection name");
const inDocumentId = op.inString("Document ID");
const inSubCollectionName = op.inString("Subcollection name");
const inLimit = op.inValue("Limit", 50);
const inUseQuery = op.inBool("Use Query");
const inQueryField = op.inString("Query Field");
const inQueryOperator = op.inDropDown("Query Operator", ["<", "<=", "==", ">", ">="], "==");
const inQueryValueAs = op.inDropDown("Query Value As", ["String", "Number", "Boolean", "Array", "Object"], "String");
const inQueryValueString = op.inString("Query Value String");
const inQueryValueNumber = op.inValue("Query Value Number");
const inQueryValueBool = op.inBool("Query Value Boolean");
const inQueryValueArray = op.inArray("Query Value Array");
const inQueryValueObject = op.inObject("Query Value Object");
const inFilterOnOrOff = op.inBool("Filter On or Off");
const inFilterKey = op.inString("Filter Key");
const inValueMustBe = op.inString("Value Must Be");

// outputs
const outSuccess = op.outTrigger("Success");
const outError = op.outTrigger("Error");
const outErrorMessage = op.outString("Error Message");
const outDocumentNotFouned = op.outValueBool("Document Not Found");
const outDocuments = op.outArray("Documents");
const outHasMore = op.outValueBool("Has More");  // New output

// global variable to hold last document for pagination
let lastDocument = null;

// events
inTrigger.onTriggered = getDocuments;
inNextPageTrigger.onTriggered = getNextPage;

function getDocuments() {
    // Reset lastDocument
    lastDocument = null;
    fetchDocuments();
}

function getNextPage() {
    fetchDocuments();
}

function fetchDocuments() {
    const db = firebase.firestore();

    let docId = inDocumentId.get();
    let subCollectionName = inSubCollectionName.get();
    let docRef = db.collection(inCollectionName.get());

    if (docId && subCollectionName) {
        docRef = docRef.doc(docId).collection(subCollectionName);
    }

    if (inUseQuery.get()) {
        if (!inQueryField.get() || !inQueryOperator.get() || !inQueryValueAs.get()) {
            outErrorMessage.set("Missing Arguments: Collection name, Query Field, Query Operator or Query Value is missing!");
            outError.trigger();
            outDocuments.set(null);
            outDocumentNotFouned.set(false);
            outHasMore.set(false);
            return;
        }
        let value = getValue();
        docRef = docRef.where(inQueryField.get(), inQueryOperator.get(), value);
    }

    if (inFilterOnOrOff.get()) {
        docRef = docRef.where(inFilterKey.get(), "==", inValueMustBe.get());
    }

    if (lastDocument) {
        docRef = docRef.startAfter(lastDocument);
    }

    // Apply the limit
    docRef = docRef.limit(inLimit.get());

    docRef.get().then((querySnapshot) => {
        if (!querySnapshot.empty) {
            let documents = [];
            querySnapshot.forEach((doc) => {
                let docData = doc.data();
                docData.id = doc.id;
                documents.push(docData);
            });
            outDocuments.set(documents);
            outDocumentNotFouned.set(false);
            outSuccess.trigger();

            // Update lastDocument for pagination
            lastDocument = querySnapshot.docs[querySnapshot.docs.length - 1];

            // If there are documents, set outHasMore to true
            outHasMore.set(true);
        } else {
            outDocuments.set(null);
            outDocumentNotFouned.set(true);
            outHasMore.set(false);  // There are no more documents
            outError.trigger();
        }
    }).catch((error) => {
        outErrorMessage.set(error.message);
        outError.trigger();
    });
}

function getValue() {
    let valueAs = inQueryValueAs.get();
    if (valueAs === "String") {
        return inQueryValueString.get();
    }
    if (valueAs === "Number") {
        return inQueryValueNumber.get();
    }
    if (valueAs === "Boolean") {
        return inQueryValueBool.get();
    }
    if (valueAs === "Array") {
        return inQueryValueArray.get();
    }
    if (valueAs === "Object") {
        return inQueryValueObject.get();
    }
}

