        }
        let value = getValue();
        docRef = docRef.where(inQueryField.get(), inQueryOperator.get(), value);
    }

    if (inFilterOnOrOff.get()) {
        docRef = docRef.where(inFilterKey.get(), "==", inValueMustBe.get());
    }

    if(lastDocument) {
        docRef = docRef.startAfter(lastDocument);
    }

    // Apply the limit
    docRef = docRef.limit(inLimit.get());

    docRef.get().then((querySnapshot) => {
        if (!querySnapshot.empty) {
            let documents = [];
            querySnapshot.forEach((doc) => {
                let docData = doc.data();
                docData.id = doc.id;
                documents.push(docData);
            });
            outDocuments.set(documents);
            outDocumentNotFouned.set(false);
            outSuccess.trigger();

            // Update lastDocument for pagination
            lastDocument = querySnapshot.docs[querySnapshot.docs.length - 1];

            // If there are documents, set outHasMore to true
            outHasMore.set(true);
        } else {
            outDocuments.set(null);
            outDocumentNotFouned.set(true);
            outHasMore.set(false);  // There are no more documents
            outError.trigger();
        }
    }).catch((error) => {
        outErrorMessage.set(error.message);
        outError.trigger();
    });
}

function getValue() {
    let valueAs = inQueryValueAs.get();
    if (valueAs === "String") {
        return inQueryValueString.get();
    }
    if (valueAs === "Number") {
        return inQueryValueNumber.get();
    }
    if (valueAs === "Boolean") {
        return inQueryValueBool.get();
    }
    if (valueAs === "Array") {
        return inQueryValueArray.get();
    }
    if (valueAs === "Object") {
        return inQueryValueObject.get();
    }
}

