// Helper function to get nested properties
function getNestedProperty(obj, path) {
  return path.split('.').reduce((prev, curr) => {
    return prev ? prev[curr] : null;
  }, obj);
}

// Helper function to convert Firestore Timestamp to display date
function convertTimestampToDate(timestamp, timeZone) {
  if (!timestamp || !timestamp.seconds) return '';
  const date = new Date(timestamp.seconds * 1000);
  const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit', timeZone: timeZone, timeZoneName: 'short' };
  return date.toLocaleDateString('en-US', options);
}

