function formatNumber(num) {
    const [whole, decimal] = num.split('.');
    const formattedWhole = Number(whole).toLocaleString('en');
    let formattedDecimal = (decimal || '').substring(0, 3);

    // If the decimal part is less than 3 digits, add trailing zeroes
    while(formattedDecimal.length < 3) {
        formattedDecimal += '0';
    }

    return `${formattedWhole}.${formattedDecimal}`;
}

