async function signUp() {
  outLoading.set(1);
  let db = firebase.firestore();

  try {
    const userCredential = await firebase.auth().createUserWithEmailAndPassword(inEmail.get(), inPassword.get());
    const user = userCredential.user;
    const uid = user.uid;

    // User Access Collection
    let userAccessDocRef = db.collection("userAccess").doc(uid);
    await userAccessDocRef.set({
      "type": "client",
      "accountStatus": "inactive",
      "statusMsg": "new",
      "uid": uid,
      "createdAt": firebase.firestore.FieldValue.serverTimestamp(),
      "emailVerified": "false",
      "lastDecision": "new",
      "lastEvent": "new",
      "inboxAccount": "false",
      "timezone": "UTC"
    });

    // Users Collection
    let usersDocRef = db.collection("users").doc(uid);
    await usersDocRef.set({
      "accountStatus": "inactive",
      "email": user.email,
      "uid": uid,
      "firstName": user.email,
      "lastName": "(unverified)",
      "icon": "https://firebasestorage.googleapis.com/v0/b/cables-5b10a.appspot.com/o/assets%2FuserProfile.png?alt=media&token=1dff1a89-7d8c-4102-828f-e2e481c95061",
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

    // Commercial Access Collection
    let commercialAccessDocRef = db.collection("commercialAccess").doc(uid);
    await commercialAccessDocRef.set({

      "uid": uid,
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

    // Veriff Collection
    let veriffDocRef = db.collection("veriff").doc(uid);
    await veriffDocRef.set({
      "lastEvent": "new",
      "lastDecision": "new",
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

        // Contacts Collection
    let contactsDocRef = db.collection("contacts").doc(uid);
    await contactsDocRef.set({
      "uid": uid,
      "createdAt": firebase.firestore.FieldValue.serverTimestamp()
    });

    // Create sub-collection 'users' inside the existing Contacts Collection
    let contactsUsersSubDocRef = contactsDocRef.collection('users').doc('NQ9WG6mMGOevVF0x1J91WZVsWIX2');
    await contactsUsersSubDocRef.set({
      "uid": "NQ9WG6mMGOevVF0x1J91WZVsWIX2"
    });


    if (!user.emailVerified) {
      user.sendEmailVerification();
      outVerificationEmailSent.set(true);
    }

    outSignUpError.set(false);
    outSignUpErrorMessage.set('');
    outLoading.set(0);
    outSuccess.trigger();
  } catch (error) {
    outLoading.set(0);
    outSignUpError.set(true);
    outSignUpErrorMessage.set(error.message);
    outFailure.trigger();
  }
}

