// Input
const inTrigger = op.inTriggerButton("Trigger");
const inCollectionName = op.inString("Collection name");
const inSubCollectionName = op.inString("SubCollection name");
const inParentDocumentID = op.inString("Parent Document ID"); // Only needed if adding to a sub-collection
const inDocument = op.inObject("Document");
const inCreateTimestamp = op.inBool("Create Timestamp");
const inIncludeDocumentID = op.inBool("Include Document ID", false); // New input boolean for including Document ID

// Output
const outError = op.outBoolNum("Error", false);
const outErrorMessage = op.outString("Error Message");
const outCreatedDocument = op.outObject("Created Document");
const outCreatedDocumentID = op.outString("Created Document ID");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered = addDocument;

async function addDocument() {
  if (!inCollectionName.get() || !inDocument.get()) {
    outError.set(true);
    outErrorMessage.set("Missing Arguments: Collection name or Document is missing!");
    return;
  }

  const db = firebase.firestore();
  let docRef;

  if (inSubCollectionName.get() && inParentDocumentID.get()) {
    docRef = db.collection(inCollectionName.get())
               .doc(inParentDocumentID.get())
               .collection(inSubCollectionName.get());
  } else {
    docRef = db.collection(inCollectionName.get());
  }

  let documentToAdd = {...inDocument.get()}; // Make a copy so we don't modify the original

  // Remove 'createdAt' if it exists and inCreateTimestamp is false
  if (!inCreateTimestamp.get() && documentToAdd.hasOwnProperty('createdAt')) {
    delete documentToAdd.createdAt;
  }

  // Add 'createdAt' timestamp if inCreateTimestamp is true
  if (inCreateTimestamp.get()) {
    documentToAdd.createdAt = firebase.firestore.FieldValue.serverTimestamp();
  }

  try {
    const doc = await docRef.add(documentToAdd);

    // If 'Include Document ID' is true, update the document to include its own ID
    if (inIncludeDocumentID.get()) {
      await doc.update({
        "documentID": doc.id
      });
      documentToAdd.documentID = doc.id; // Add the document ID to our local copy
    }

    outError.set(false);
    outErrorMessage.set("");
    outCreatedDocument.set(documentToAdd);
    outCreatedDocumentID.set(doc.id);
    outSuccess.trigger();
  } catch (error) {
    outError.set(true);
    outCreatedDocument.set(null);
    outCreatedDocumentID.set(null);
    outErrorMessage.set(error.message);
    outFailure.trigger();
  }
}

// input
const inTrigger = op.inTriggerButton("Trigger");
const inCollectionName = op.inString("Collection name");
const inDocumentId = op.inString("Document ID"); // New input for document ID
