// Inputs
const inArray = op.inArray("Array");
const inNewKeyName = op.inString("New Key Name");
const inKey1 = op.inString("Key 1");
const inKey2 = op.inString("Key 2");
const inKey3 = op.inString("Key 3");
const inKey4 = op.inString("Key 4");

// Output
const outResultArray = op.outArray("Result Array");

// Triggers
const outSuccess = op.outTrigger("Success");
const outFail = op.outTrigger("Fail");

// Event handler
inArray.onChange = function() {
    // Get the input array
    const inputArray = inArray.get();

    // Check if the input array is null
    if (inputArray === null) {
        outResultArray.set(null);
        outFail.trigger();
        return;
    }

    // Check if the input array is not an array or is empty
    if (!Array.isArray(inputArray) || inputArray.length === 0) {
        outFail.trigger();
        return;
    }

    // Initialize the keys
    const newKeyName = inNewKeyName.get();
    const key1 = inKey1.get();
    const key2 = inKey2.get();
    const key3 = inKey3.get();
    const key4 = inKey4.get();

    // Initialize result array
    const resultArray = [];

    // Iterate through the input array and modify each object
    for (let obj of inputArray) {
        // Clone the object so we don't modify the original
        const newObj = { ...obj };

        // Build the new key-value
        let newKeyValue = "";
        if (key1 && obj[key1]) newKeyValue += obj[key1];
        if (key2 && obj[key2]) newKeyValue += " " + obj[key2];
        if (key3 && obj[key3]) newKeyValue += " " + obj[key3];
        if (key4 && obj[key4]) newKeyValue += " " + obj[key4];

        // Remove leading and trailing spaces
        newKeyValue = newKeyValue.trim();

        // Add new key-value to the new object
        if (newKeyName && newKeyValue !== "") {
            newObj[newKeyName] = newKeyValue;
        }

        // Add new object to the result array
        resultArray.push(newObj);
    }

    // Output the result array
    outResultArray.set(resultArray);
    outSuccess.trigger();
};

