const inTrigger = op.inTriggerButton("Trigger"); // Trigger to initiate the search
const inArray = op.inArray("Input Array"); // The input array to look into
const inLookingForKey = op.inString("Looking for Key"); // The key whose value we want to check
const inIfValueEquals = op.inString("If value equals"); // The value to match against
const inResultKey = op.inString("Result Key"); // The key whose value we want to output

// Outputs
const outResultValue = op.outString("Result Value"); // The value of the Result Key
const outSuccess = op.outTrigger("Success"); // Triggered if the operation succeeds
const outFailure = op.outTrigger("Failure"); // Triggered if the operation fails
const outError = op.outString("Error"); // Error description if the operation fails

// Main function to process the inputs
function main() {
  // Clear the previous output
  outResultValue.set(null);
  outError.set(null);

  const array = inArray.get();
  const lookingForKey = inLookingForKey.get();
  const ifValueEquals = inIfValueEquals.get();
  const resultKey = inResultKey.get();

  // Validate inputs
  if (!array || !lookingForKey || !ifValueEquals || !resultKey) {
    outError.set("Missing required inputs.");
    outFailure.trigger();
    return;
  }

  // Search for the target object in the array
  for (let i = 0; i < array.length; i++) {
    const obj = array[i];
    if (obj[lookingForKey] === ifValueEquals) {
      // Found the target object, now get the value of the result key
      const resultValue = obj[resultKey];
      if (resultValue !== undefined) {
        outResultValue.set(resultValue);
        outSuccess.trigger();
        return;
      }
    }
  }

  // If we reach here, the operation has failed
  outError.set("Target object not found or Result Key not found.");
  outFailure.trigger();
}

// Bind function to the trigger
inTrigger.onTriggered = main;

