// Function to convert XRPL timestamps to human-readable dates
function convertXRPLTimestampsToReadableDates(timestamps, format, timeZone) {
  const convertedDates = [];

  timestamps.forEach((timestamp) => {
    const jsTimestamp = (timestamp + 946684800) * 1000; // Convert XRPL timestamp to JavaScript timestamp
    const date = new Date(jsTimestamp);
    const options = {
      timeZone,
      year: format.includes("year") ? "numeric" : undefined,
      month: format.includes("month") ? "short" : undefined,
      day: format.includes("day") ? "numeric" : undefined,
      hour: format.includes("hour") ? "2-digit" : undefined,
      minute: format.includes("minute") ? "2-digit" : undefined,
      second: format.includes("second") ? "2-digit" : undefined
    };

    const readableDate = new Intl.DateTimeFormat("en-US", options).format(date);
    convertedDates.push(readableDate);
  });

  return convertedDates;
}

