**/



const
    // Inputs
    inBool1 = op.inValue("Bool1"),
    inBool2 = op.inValue("Bool2"),
    trueOutputA = op.inValue("True OutputA"),
    trueOutputB = op.inValue("True OutputB"),
    falseOutputA = op.inValue("False OutputA"),
    falseOutputB = op.inValue("False OutputB"),

    // Outputs
    outputA = op.outValue("OutputA"),
    outputB = op.outValue("OutputB");

// Update output when any input changes
inBool1.onChange = inBool2.onChange = trueOutputA.onChange = trueOutputB.onChange = falseOutputA.onChange = falseOutputB.onChange = updateOutput;

function updateOutput() {
    let bool1 = inBool1.get();
    let bool2 = inBool2.get();

    // Check if Bool1 is true
    if (bool1 === true) {
        outputA.set(trueOutputA.get());
        outputB.set(trueOutputB.get());
    } 
    // Check if Bool1 is false
    else {
        if (bool2 === true) {
            outputA.set(trueOutputA.get());
            outputB.set(trueOutputB.get());
        } else {
            outputA.set(falseOutputA.get());
            outputB.set(falseOutputB.get());
        }
    }
}

