//input
const inTriggerSignUp = op.inTriggerButton("Sign up");
const inEmail = op.inString("email");
const inPassword = op.inString("password");
const inFirstName = op.inString("First Name");
const inLastName = op.inString("Last Name");

//output
const outSignUpErrorMessage = op.outString('Sign Up Error Message', '');
const outSignUpError = op.outValueBool('Sign Up Error', false);
const outEmailInUseError = op.outValueBool('Email in use',false);
const outInvalidEmail = op.outValueBool('Invalid Email',false);
const outOperationInvalid = op.outValueBool('Operation Invalid',false);
const outNoFirstName = op.outValueBool('No First Name',false);
const outNoLastName = op.outValueBool('No Last Name',false);
const outWeakPassword = op.outValueBool('Weak Password',false);
const outLoading = op.outBoolNum("Loading", 0);
const outVerificationEmailSent = op.outValueBool(false);
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTriggerSignUp.onTriggered = signUp;

async function signUp() {
  if (!inFirstName.get() && inLastName.get()) {
    outSignUpError.set(true);
    outSignUpErrorMessage.set('Missing First Name');
    outNoFirstName.set(true);
    outNoLastName.set(false);
    outFailure.trigger();
    return;
  } else if (inFirstName.get() && !inLastName.get()) {
    outSignUpError.set(true);
    outSignUpErrorMessage.set('Missing Last Name');
    outNoFirstName.set(false);
    outNoLastName.set(true);
    outFailure.trigger();
    return;
  } else if (!inFirstName.get() && !inLastName.get()) {
    outSignUpError.set(true);
    outSignUpErrorMessage.set('Missing First Name');
    outSignUpErrorMessage.set('Missing Last Name');
    outNoFirstName.set(true);
    outNoLastName.set(true);
    outFailure.trigger();
    return;
  }
  outNoLastName.set(false);
  outNoFirstName.set(false);

  outLoading.set(1);
  let db = firebase.firestore();
  await firebase.auth().createUserWithEmailAndPassword(inEmail.get(), inPassword.get())
    .then(async (userCredential) => {
      let docRef = db.collection("users").doc(userCredential.user.uid);
      let user = userCredential.user;
      await docRef.set({
        "userProfile": {
          "email": user.email,
          "uid": user.uid,
          "firstName": inFirstName.get(),
          "lastName": inLastName.get()
        },
        "userHas": {
          "defaultWallet": "false",
          "profilePhoto": "false",
          "verifiedEmail": "false",
          "verifiedPhone": "false"
        },
        "created": {
          "createdDate": firebase.firestore.FieldValue.serverTimestamp(),
          "createdByUid": user.uid
        }
      })

      if (!user.emailVerified) {
        user.sendEmailVerification();
        outVerificationEmailSent.set(true);
      }
      resetErrors();
      outLoading.set(0);
      outSuccess.trigger();
    })
    .catch((error) => {
      outLoading.set(0);
      setCorrectError(error);

      outFailure.trigger();
    });
}

function resetErrors() {
  outSignUpErrorMessage.set('');
  outSignUpError.set(false);
  outEmailInUseError.set(false);
  outInvalidEmail.set(false);
  outOperationInvalid.set(false);
  outWeakPassword.set(false);
}

function setCorrectError(error) {
  outSignUpError.set
}

