// input
const inTrigger = op.inTriggerButton('Trigger');
const inBucket = op.inString('Bucket');
const inPrefix = op.inString('Prefix');
const inMaxResults = op.inFloat('Max Results', 1000);
const inToken = op.inString('Token');
// output
const outError = op.outBool('Error');
const outErrorMessage = op.outString('Error Message');
const outFiles = op.outObject('Files');
const outToken = op.outString('Token');
const outSuccess = op.outTrigger('Success');
const outFailure = op.outTrigger('Failure');

inTrigger.onTriggered = listFiles;

function listFiles() {
  if (!inBucket.get()) {
    outError.set(true);
    outErrorMessage.set('Missing argument: Bucket is missing!');
    outFailure.trigger();
    return;
  }

  const storageRef = firebase.storage().ref();
  const listRef = storageRef.child(inPrefix.get());
  const maxResults = inMaxResults.get();

  let query = listRef.list({
    maxResults: maxResults,
    pageToken: inToken.get()
  });

  query.then((result) => {
    let files = [];
    result.items.forEach((itemRef) => {
      files.push({
        name: itemRef.name,
        fullPath: itemRef.fullPath,
        contentType: itemRef.contentType,
        size: itemRef.size,
        timeCreated: itemRef.timeCreated,
        updated: itemRef.updated
      });
    });

    outFiles.set(files);
    outToken.set(result.nextPageToken || '');
    outError.set(false);
    outErrorMessage.set('');
    outSuccess.trigger();
  })
  .catch((error) => {
    outError.set(true);
    outErrorMessage.set(error.message);
    outFailure.trigger();
  });
}

