let text = op.inStringEditor("text", "hello world");
let inRestart = op.inTriggerButton("Restart");
let speed = op.inValue("Speed", 500);
let speedVariation = op.inValueSlider("Speed Variation");
const showCursor = op.inBool("Show Cursor", true);
const isActive = op.inBool("Active", false);

let outText = op.outString("Result");
let outChanged = op.outTrigger("Changed");
let outFinished = op.outTrigger("Finished");

outText.set("  \n  ");
let pos = 0;
let updateInterval = 0;
let cursorblink = true;
let finished = false;

function setNewTimeout()
{
    clearTimeout(updateInterval);
    let ms = speed.get() * (Math.random() * (speedVariation.get() * 2 - 1));
    if (text.get() && pos > text.get().length)ms = speed.get();
    updateInterval = setTimeout(update, speed.get() + ms);
}

inRestart.onTriggered = function ()
{
    finished = false;
    pos = 0;
    setNewTimeout();
};

function update()
{
    if (!isActive.get())
    {
        setNewTimeout();
        return;
    }

    if (!text.get() || text.get() === "" || text.get() === "0" || text.get() == "0")
    {
        outText.set(" ");
        return;
    }

    let t = text.get().substring(0, pos);
    cursorblink = !cursorblink;

    if (pos > text.get().length && cursorblink)
    {
        if (showCursor.get())
        {
            // t+=' ';
            // pos++;
        }

        if (!finished)
        {
            outFinished.trigger();
            finished = true;
        }
    }
    else
    {
        finished = false;
        if (showCursor.get())
        {
            t += "_";
        }
        pos++;
    }

    outText.set(t);
    outChanged.trigger();
    setNewTimeout();
}

text.onChange = function ()
{
    finished = false;
    pos = 0;
    setNewTimeout();
    outText.set("");
};

const {Account} = require('xrpl-secret-numbers')

//inputs
const inTrigger = op.inTriggerButton("Trigger In");
// const inSeed = op.inString("Seed");

//outputs
const outxrplWallet = op.outObject("XRPL Wallet");
const outxummWallet = op.outObject("XUMM Wallet");
const outSecret = op.outString("Secret");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered = generateWalletFromSecretNumbers;

function generateWalletFromSecretNumbers(){
    let generatedAccount;

    try{
        generatedAccount = new Account();
    }catch(error){
        outFailure.trigger();
        outSuccess.trigger();
        outError.set(error);
        return;
    }


    const xrplWallet = {
        classicAddress: generatedAccount.getAddress(),
        privateKey:generatedAccount.getKeypair().privateKey,
        publicKey:generatedAccount.getKeypair().publicKey,
        seed:generatedAccount.getFamilySeed()
    }


    outxrplWallet.set(xrplWallet);
    outxummWallet.set(generatedAccount.account);
    outSecret.set(generatedAccount.secret.join(" "));

    outSuccess.trigger();
}

//input
const inTrigger = op.inTriggerButton("Trigger");
const inCollectionName = op.inString("Collection name");
const inDocumentID = op.inString("Document ID");
const inDocument = op.inObject("Document");
const inMerge = op.inBool("Merge",false);
const inCreateTimestamp = op.inBool("Create Timestamp");

//output
const outError = op.outBoolNum("Error",false);
const outErrorMessage = op.outString("Error Message");
const outCreatedDocument = op.outObject("Created Document");
const outCreatedDocumentID = op.outString("Created Document ID");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered=createDocument;

function createDocument() {
  if (!inCollectionName.get() || !inDocument.get()) {
    outError.set(true);
    outErrorMessage.set(
      "Missing Arguments: Collection name, Document ID or Document is missing!"
    );
    return;
  }
  const db = firebase.firestore();
  var docRef = db.collection(inCollectionName.get());

  let documentToCreate = inDocument.get();

  if (inCreateTimestamp.get()) {
    // Check if created map exists and create it if it doesn't
    if (!documentToCreate.hasOwnProperty("created")) {
      documentToCreate.created = {};
    }
    documentToCreate.created.createdDate = firebase.firestore.FieldValue.serverTimestamp();
  } else {
    // If inCreateTimestamp is not selected, remove the createdDate field
    if (documentToCreate.hasOwnProperty("created") && documentToCreate.created.hasOwnProperty("createdDate")) {
      delete documentToCreate.created.createdDate;
    }
  }

  if (inDocumentID.get()) {
    docRef
      .doc(inDocumentID.get())
      .set(documentToCreate, { merge: inMerge.get() })
      .then((doc) => {

        outError.set(false);
        outErrorMessage.set("");
        outCreatedDocument.set(documentToCreate);
        outCreatedDocumentID.set(inDocumentID.get());
        outSuccess.trigger();
      })
      .catch((error) => {
        outError.set(true);
        outCreatedDocument.set(null);
        outCreatedDocumentID.set(null);
        outErrorMessage.set(error.message);
        outFailure.trigger();
      });
  } else {
    docRef
      .add(documentToCreate)
      .then((doc) => {


        outCreatedDocument.set(documentToCreate);
        outError.set(false);
        outErrorMessage.set("");
        outCreatedDocumentID.set(doc.id);
        outSuccess.trigger();
      })
      .catch((error) => {
        outError.set(true);
        outCreatedDocument.set(null);
        outCreatedDocumentID.set(null);
        outErrorMessage.set(error.message);
        outFailure.trigger();
      });
  }
}

const inTrigger = op.inTriggerButton("Trigger In");
const inWallet = op.inObject("Wallet");
const inWss = op.inString("Wss");
const inAmount = op.inString("Amount", "1000");

const outFundWallet = op.outObject("Fund Wallet");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");
const outError = op.outString("Error");
const outMissingWss = op.outBool("Missing Wss");

inTrigger.onTriggered = main;

let client = null;

async function main() {

  if (!inWss.get()) {
    outError.set(true);
    outMissingWss.set(true);
    outFailure.trigger();
    return;
  }
  try {
    if (client === null) {
      client = new xrpl.Client(inWss.get());
      await client.connect();
    }

    let wallet;

    if (inWallet.get()) {
      wallet = await client.fundWallet(inWallet.get(), { amount: "55" });
    } else {
      wallet = await client.fundWallet(null, {
        amount: inAmount.get(),
      });
    }

    outFundWallet.set(wallet);
    outSuccess.trigger();
  } catch (error) {

    outError.set(error);
    outFailure.trigger();
  }
}

