// input
const inTrigger = op.inTriggerButton('Trigger');
const inFile = op.inString('File (Base64)');
const inPath = op.inString('Path');
const inFilename = op.inString('Filename');
const inMetadata = op.inObject('Metadata');
// output
const outError = op.outBool('Error');
const outErrorMessage = op.outString('Error Message');
const outSuccess = op.outTrigger('Success');
const outFailure = op.outTrigger('Failure');

inTrigger.onTriggered = uploadFile;

function uploadFile() {
    if (!inFile.get()) {
        outError.set(true);
        outErrorMessage.set('Missing argument: File is missing!');
        outFailure.trigger();
        return;
    }
    if (!inPath.get()) {
        outError.set(true);
        outErrorMessage.set('Missing argument: Path is missing!');
        outFailure.trigger();
        return;
    }
    if (!inFilename.get()) {
        outError.set(true);
        outErrorMessage.set('Missing argument: Filename is missing!');
        outFailure.trigger();
        return;
    }

    const storageRef = firebase.storage().ref();
    const fileRef = storageRef.child(`${inPath.get()}/${inFilename.get()}`);
    const metadata = inMetadata.get();
    const base64 = `data:image/png;base64,${inFile.get()}`;
    const uploadTask = fileRef.putString(base64, 'data_url', metadata);

    uploadTask.then(() => {
        outError.set(false);
        outErrorMessage.set('');
        outSuccess.trigger();
    }).catch((error) => {
        outError.set(true);
        outErrorMessage.set(error.message);
        outFailure.trigger();
    });
}

