const inFirstChars = op.inValue("Number of First Characters to Show", 10, {min: 1, max: 100});
const inLastChars = op.inValue("Number of Last Characters to Show", 10, {min: 1, max: 100});
const outStr = op.outString("Output String");

inStr.onChange = update;
inFirstChars.onChange = update;
inLastChars.onChange = update;

function update() {
  const str = inStr.get();
  const firstChars = inFirstChars.get();
  const lastChars = inLastChars.get();

  if (str.length <= firstChars + lastChars) {
    outStr.set(str);
  } else {
    const firstPart = str.substring(0, firstChars);
    const lastPart = str.substring(str.length - lastChars);
    outStr.set(`${firstPart}...${lastPart}`);
  }
}

