const inRawString = op.inString("Raw String");
const inDropsShown = op.inValue("Drops Shown", 6, {min: 0, max: 6});
const inCurrencyCode = op.inString("Currency Code", "XRP");
const outString = op.outString("Result String");
const outXrpValue = op.outValue("XRP Value");
const outDropsValue = op.outValue("Drops Value");

function update() {
  const rawString = inRawString.get();
  const dropsShown = inDropsShown.get();
  const currencyCode = inCurrencyCode.get();

  if (!rawString || isNaN(rawString)) {
    outString.set("");
    outXrpValue.set(0);
    outDropsValue.set(0);
    return;
  }

  const dropsValue = parseInt(rawString, 10);
  const xrpValue = dropsValue / 1000000;
  const xrpValueFixed = xrpValue.toFixed(dropsShown);
  const resultString = `${xrpValueFixed} ${currencyCode}`;

  outString.set(resultString);
  outXrpValue.set(xrpValue);
  outDropsValue.set(dropsValue);
}

inRawString.onChange = update;
inDropsShown.onChange = update;
inCurrencyCode.onChange = update;

update();

