const inTrigger = op.inTriggerButton("Trigger In");
const inWss = op.inString("WSS URL");
const inSeed = op.inString("Sender Secret Key");
const inDestination = op.inString("Destination Account");
const inAmount = op.inValue("Amount (XRP)", 1);

const outTransactionHash = op.outString("Transaction Hash");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");
const outError = op.outString("Error");
const outMissingFields = op.outBool("Missing Fields");

inTrigger.onTriggered = main;
let client = null;

async function main() {


  // Check if required fields are missing
  if (!inWss.get() || !inSeed.get() || !inDestination.get()) {
    outError.set("Missing fields");
    outMissingFields.set(true);
    outFailure.trigger();
    return;
  }

  // Define the network client
  try {
    if (client === null) {
      client = new xrpl.Client(inWss.get());
      await client.connect();
    }

    const wallet = xrpl.Wallet.fromSeed(inSeed.get());

    // Prepare transaction object
    const prepared = await client.autofill({
      TransactionType: "Payment",
      Account: wallet.classicAddress,
      Destination: inDestination.get(),
      Amount: xrpl.xrpToDrops(inAmount.get().toString()),
    });



    // Sign prepared instructions

    const signed = wallet.sign(prepared);


    // Submit signed transaction
    const submitResult = await client.submitAndWait(signed.tx_blob);

    outTransactionHash.set(submitResult.result.hash);
    outSuccess.trigger();
  } catch (error) {

    outError.set(error.message);
    outFailure.trigger();
  } finally {
    if (client !== null) {
      client.disconnect();
      client = null;
    }
  }
}

const inTrigger = op.inTriggerButton("Trigger In");
const inWss = op.inString("WSS URL");
const inAddress = op.inString("XRPL Address");

const outAccount = op.outObject("Account");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");
const outError = op.outString("Error");
const outMissingFields = op.outBool("Missing Fields");

inTrigger.onTriggered = main;
let client = null;

async function main() {


  // Check if required fields are missing
  if (!inWss.get() || !inAddress.get()) {
    outError.set("Missing fields");
    outMissingFields.set(true);
    outFailure.trigger();
    return;
  }

  // Define the network client
  try {
    if (client === null) {
      client = new xrpl.Client(inWss.get());
      await client.connect();
    }

    // Fetch account details using GetAccountInfo API
    const accountInfo = await client.request({
      command: "account_info",
      account: inAddress.get()
    });



    outAccount.set(accountInfo);

    outSuccess.trigger();
  } catch (error) {

    outError.set(error.message);
    outFailure.trigger();
  } finally {
    if (client !== null) {
      client.disconnect();
      client = null;
    }
  }
}

const inRawString = op.inString("Raw String");
const inDropsShown = op.inValue("Drops Shown", 6, {min: 0, max: 6});
const inCurrencyCode = op.inString("Currency Code", "XRP");
const outString = op.outString("Result String");
const outXrpValue = op.outValue("XRP Value");
const outDropsValue = op.outValue("Drops Value");

function update() {
  const rawString = inRawString.get();
  const dropsShown = inDropsShown.get();
  const currencyCode = inCurrencyCode.get();

  if (!rawString || isNaN(rawString)) {
    outString.set("");
    outXrpValue.set(0);
    outDropsValue.set(0);
    return;
  }

  const dropsValue = parseInt(rawString, 10);
  const xrpValue = dropsValue / 1000000;
  const xrpValueFixed = xrpValue.toFixed(dropsShown);
  const resultString = `${xrpValueFixed} ${currencyCode}`;

  outString.set(resultString);
  outXrpValue.set(xrpValue);
  outDropsValue.set(dropsValue);
}

inRawString.onChange = update;
inDropsShown.onChange = update;
inCurrencyCode.onChange = update;

update();

