const inputs = [];
const outputs = [];

for(let i = 0; i < 10; i++) {
  inputs[i] = op.inValue(`Number ${i+1}`);
  outputs[i] = op.outString(`Result ${i+1}`);
}

for(let i = 0; i < 10; i++) {
  inputs[i].onChange = update.bind(null, i);
  update(i);
}

function update(index) {
  const value = inputs[index].get() || 0;
  outputs[index].set(String(value));
}

const inTrigger = op.inTriggerButton("Trigger In");
const inWallet = op.inObject("Wallet");
const inWss = op.inString("Wss");
const inAmount = op.inString("Amount", "1000");

const outFundWallet = op.outObject("Fund Wallet");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");
const outError = op.outString("Error");
const outMissingWss = op.outBool("Missing Wss");

inTrigger.onTriggered = main;

let client = null;

async function main() {

  if (!inWss.get()) {
    outError.set(true);
    outMissingWss.set(true);
    outFailure.trigger();
    return;
  }
  try {
    if (client === null) {
      client = new xrpl.Client(inWss.get());
      await client.connect();
    }

    let wallet;

    if (inWallet.get()) {
      wallet = await client.fundWallet(inWallet.get(), { amount: "55" });
    } else {
      wallet = await client.fundWallet(null, {
        amount: inAmount.get(),
      });
    }

    outFundWallet.set(wallet);
    outSuccess.trigger();
  } catch (error) {

    outError.set(error);
    outFailure.trigger();
  }
}

const {Account} = require('xrpl-secret-numbers')

//inputs
const inTrigger = op.inTriggerButton("Trigger In");
// const inSeed = op.inString("Seed");

//outputs
const outxrplWallet = op.outObject("XRPL Wallet");
const outxummWallet = op.outObject("XUMM Wallet");
const outSecret = op.outString("Secret");
const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered = generateWalletFromSecretNumbers;

function generateWalletFromSecretNumbers(){
    let generatedAccount;

    try{
        generatedAccount = new Account();
    }catch(error){
        outFailure.trigger();
        outSuccess.trigger();
        outError.set(error);
        return;
    }


    const xrplWallet = {
        classicAddress: generatedAccount.getAddress(),
        privateKey:generatedAccount.getKeypair().privateKey,
        publicKey:generatedAccount.getKeypair().publicKey,
        seed:generatedAccount.getFamilySeed()
    }


    outxrplWallet.set(xrplWallet);
    outxummWallet.set(generatedAccount.account);
    outSecret.set(generatedAccount.secret.join(" "));

    outSuccess.trigger();
}

