const outSuccess = op.outTrigger("Success");
const outFailure = op.outTrigger("Failure");

inTrigger.onTriggered = updateDocument;

function updateDocument() {
  if (
    !inCollectionName.get() ||
    !inDocumentID.get() ||
    !inTimestampPath.get()
  ) {
    outError.set(true);
    outErrorMessage.set(
      "Missing Arguments: Collection name, Document ID, or timestamp path is missing!"
    );
    outFailure.trigger();
    return;
  }

